mapper.select:
select * from s_student


        name = #{student.name}


        and clazz_id = #{student.clazz_id}


        and id = #{student.id}


limit #{page},#{limit}

select count(*) as total from s_student


        name = #{name}


        and clazz_id = #{clazz_id}


        and id = #{id}

mapper.update:
update s_student
set name=#{name}, sex=#{sex}, mobile=#{mobile}, qq=#{qq}, clazz_id=#{clazz_id}
where id=#{id}

mapper.delete:
delete from s_student

    where id in

        #{id}
